const jobInfo = [
  {
    title: "Front End Web Developer",
    start: "April 2018",
    end: "Present",
    company: "BMT Micro, Inc.",
    location: "Wilmington, NC",
    description: "BMT Micro is an e-commerce company for software and game developers. Since starting with BMT Micro, I have created a custom WordPress plugin, designed a new company logo, redesigned and rebuilt all of the company websites, and designed and built our custom email template. I helped implement a new 'pop-up' shopping cart, by helping to design and code the front end. After completion of the new company homepage, we have seen traffic triple, with an increase in new accounts created.",
    url: "https://www.bmtmicro.com/"
  },
  {
    title: "UI/UX Developer (Contract)",
    start: "December 2017",
    end: "April 2018",
    company: "TRU Colors Brewing",
    location: "Wilmington, NC",
    description: "A local startup that will ultimately be run and operated by active gang members - working together to end gang-on-gang violence in our area. My primary responsibilities were to create their company website, consult on ad design questions, and create their brochure to distribute to potential partners and investors. The technology I used to handle the work consists of HTML, CSS, Bootstrap, Javascript, jQuery, Photoshop, InDesign, Illustrator, and Sketch.",
    url: "https://www.trucolors.co/"
  },
  {
    title: "Front End Web Developer",
    start: "January 2017",
    end: "December 2017",
    company: "JOMO, Inc.",
    location: "Wilmington, NC",
    description: "JOMO was a local mobile app startup, where I was in charge of creating and maintaining their website and additional webpages. Aside from creating, updating, and maintaining the website, I was heavily involved in the daily graphic work that was used for advertising and website content. To create the website and ads, I relied on HTML, CSS, Bootstrap, Javascript, jQuery, Branch.io, Photoshop, Illustrator, and Sketch.",
    url: "https://jeffberlin.github.io/JOMO_website/index.html"
  },
  {
    title: "Operations Manager",
    start: "September 2015",
    end: "January 2017",
    company: "No Limit Customs",
    location: "North Myrtle Beach, SC",
    description: "Even though No Limit Customs is a custom automotive shop, I was in charge of project scheduling, handling online sales, shipments, inventory, and also creating the designs used for the online shopping stores and advertisements.",
  },
];

export default jobInfo;
