import { useEffect } from 'react';

function ScrollTop() {
  return (
    <div>
      <button
        onClick={() => {
          window.scrollTo({ top: 0, left: 0, behavior: 'smooth' })
        }}
        className="scrolltop"

      >
        <i className="fa fa-angle-up"></i>
      </button>
    </div>
  );
}

export default ScrollTop;
