import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Image from 'react-bootstrap/Image';
import MyHashLink from './MyHashLink';
import headshot from '../../images/jeff-berlin-landing-background-min3.jpg';
import { Nav } from 'react-bootstrap';
import MyContactModal from './ContactModal';

function Landing() {
  const [modalShow, setModalShow] = React.useState(false);
  return (
    <>
      <Container fluid className='landing' style={{ backgroundImage: `url(${headshot})` }}>
        <Row>
          <Col xl={6} lg={6} md={8} style={{ paddingLeft: '0'}}>
            <div className='name-box'>
              <h1>Jeff Berlin</h1>
              <h2>UI/UX Developer & Designer</h2>
              <p style={{ color: '#fff' }}>I am a highly motivated UI/UX Developer and Designer that loves learning and problem solving. Racing cars, snowboarding, and playing ice hockey just help to pass the time.</p>
              <div className='text-center'>
                <button
                  className='contact-btn'
                  onClick={() => setModalShow(true)}
                >
                  Contact Me
                </button>
              </div>
            </div>
          </Col>
        </Row>
        <Row className='justify-content-center'>
          <div className='text-center' style={{ bottom: '0', position: 'absolute'}}>
            <Nav.Link
              to="/#portfolio"
              as={MyHashLink}
            >
              <i className='fa fa-chevron-down' style={{ opacity: '.75' }}></i>
            </Nav.Link>
          </div>
        </Row>
      </Container>

      <MyContactModal 
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </>
  );
}

export default Landing;
