import projects from './ProjectsArray';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion';
import { useAccordionButton } from 'react-bootstrap/AccordionButton';
import { useContext, useState } from 'react';
import AccordionContext from 'react-bootstrap/AccordionContext';

function CustomToggle({ children, eventKey, callback }) {
  const { activeEventKey } = useContext(AccordionContext);

  const decoratedOnClick = useAccordionButton(
    eventKey,
    () => callback && callback(eventKey),
  );

  const isCurrentEventKey = activeEventKey === eventKey;

  return (
    <button
      type="button"
      className={isCurrentEventKey ? 'close-project' : 'open-project'}
      onClick={decoratedOnClick}
    >
      {/* {isCurrentEventKey ? 'Close' : 'Read More'} */}
      {isCurrentEventKey ? 'X' : '+'}
    </button>
  );
}

function Portfolio() {
  return (
    <section className="projects" style={{ paddingTop: '3rem' }}>
      <Container style={{ padding: '3rem' }}>
        <Row>
          <Col lg={10}>
            <h2 className="section-title">Projects</h2>
          </Col>
        </Row>
        <Accordion>
          <Row>
            {projects.map((project, index) => (
              <Col lg={3} key={index}>
                <Card>
                  <Card.Img variant="top" src={project.image} />
                  <Card.Body>
                    <Card.Title>{project.title}</Card.Title>
                    <CustomToggle eventKey={project.id}></CustomToggle>
                    <Accordion.Collapse eventKey={project.id}>
                      <Card.Text>{project.description}</Card.Text>
                    </Accordion.Collapse>
                  </Card.Body>
                  {/* <a href="" className="card-footer-button">View&nbsp;{project.type}</a> */}
                  <Card.Footer className="text-center">
                    <a href={project.url} target="_blank">View&nbsp;{project.type}</a>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </Accordion>
      </Container>
    </section>
  );
}

export default Portfolio;
