import { useContext, useState } from 'react';
import projects from './ProjectsArray';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion';
import { useAccordionButton } from 'react-bootstrap/AccordionButton';
import AccordionContext from 'react-bootstrap/AccordionContext';

// For the Accordion
function CustomToggle({ children, eventKey, callback }) {
  const { activeEventKey } = useContext(AccordionContext);

  const decoratedOnClick = useAccordionButton(
    eventKey,
    () => callback && callback(eventKey),
  );

  const isCurrentEventKey = activeEventKey === eventKey;

  return (
    <button
      type="button"
      className={isCurrentEventKey ? 'close-project' : 'open-project'}
      onClick={decoratedOnClick}
    >
      {isCurrentEventKey ? 'Close' : 'Read More'}
    </button>
  );
}

function Portfolio() {
  // Modal
  // const [show, setBmtLogoShow] = useState(false);

  return (
    <section className="projects" id="portfolio" style={{ paddingTop: '3rem' }}>
      <Container className="section-container">
        <Row>
          <Col lg={10}>
            <h2 className="section-title">Projects</h2>
          </Col>
        </Row>
        <Accordion>
          <Row>
            {projects.map((project, index) => (
              <Col lg={3} md={3} key={index}>
                <Card>
                  <Card.Img variant="top" src={project.image} />
                  <Card.Body>
                    <Card.Title>{project.title}</Card.Title>
                    <Card.Subtitle style={{ fontSize: '.8rem', fontWeight: '400' }}>{project.category}</Card.Subtitle>
                    <CustomToggle eventKey={project.id}></CustomToggle>
                    <Accordion.Collapse eventKey={project.id}>
                      <Card.Text>{project.description}</Card.Text>
                    </Accordion.Collapse>
                  </Card.Body>
                  <a href={project.url} target={project.target} className="card-footer-button">View&nbsp;{project.type}</a>
                </Card>
              </Col>
            ))}
          </Row>
        </Accordion>
      </Container>
      {/* <BmtLogo
        show={show}
        onHide={() => setBmtLogoShow(false)}
      /> */}
    </section>
  );
}

export default Portfolio;
