import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Image from 'react-bootstrap/Image';
import MyHashLink from './MyHashLink';
import headshot from '../../images/jeff-berlin-landing-background-min3.jpg';

function Landing() {
  return (
  <div style={{ backgroundColor: '#4f4f4f'}}>
    <Container fluid className='landing' style={{ backgroundImage: `url(${headshot})` }}>
      <Row style={{ marginTop: '55vh'}}>
        <Col xl={6} lg={6} md={8} sm={8} style={{ paddingLeft: '0'}}>
          <div style={{ padding: '20px', backgroundColor: '#007B70', border: '1px solid rgba(0, 0, 0, 0.7)',
boxShadow: '2px 2px 2px 1px #B89000'}}>
            <h1>Jeff Berlin</h1>
            <h2>UI/UX Developer & Designer</h2>
            <p style={{ color: '#fff' }}>I am a highly motivated UI/UX Developer and Designer that loves learning and problem solving. Racing cars, snowboarding, and playing ice hockey just help to pass the time.</p>
            <div className='text-center'>
              <button
                style={{ backgroundColor: '#000', color: '#fff', border: 'none', borderRadius: '10px', padding: '.5rem 1rem'}}
                to="/#portfolio"
                as={MyHashLink}
              >
                Contact Me
              </button>
            </div>
          </div>
        </Col>
      </Row>
      <Row className='justify-content-center'>
        <div className='text-center' style={{ bottom: '0', position: 'absolute'}}>
          <i className='fa fa-chevron-down' style={{ fontSize: '4rem', opacity: '.75' }}></i>
        </div>
      </Row>
    </Container>
    </div>
  );
}

export default Landing;
